sp_widget.client_script:
function($scope, nowAttachmentHandler, $animate, $rootScope, cabrillo, $timeout, snRecordWatcher, spUtil, spAriaUtil, $http, $window, $document, $sce) {
	var c = this;
	var data = [5,10];
	c.text = "Select Management URL, Datacenter and click on Apply to filter the results";
