		c.text = "Filtered Data for "+obj.account+" and "+obj.dc;
		c.selection = obj.action;
		data = [obj.serverCount,obj.vmCount];


		var chart1 = new Highcharts.chart({
			chart: {
				renderTo: 'custom_report',
				type: 'column'
			},
			title: {
				text: null
			},
			xAxis: {
				categories: ['RHV Server','RHV VMs'],
				title: {
					text: null
				},
				labels: {
					rotation: -45
				}
			},
			yAxis: {
				min: 0,
				title: {
					text: 'Count'
				}
			},
			series: [{
				'name': 'RHV Count',
				'legendIndex': 0,
				'data': data
			}]
	/*		series: [{
				'name': 'RHV Server',
				'legendIndex': 0,
				'data': data
			},
							 {
								 'name': 'RHV VMs',
								 'legendIndex' : 1,
								 'data' : [0,0]
							 }]
							 */
		});

	});


}
